import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import { firestore } from '../firebase';

const HomePage = () => {
  const [products, setProducts] = useState([]);

  useEffect(() => {
    const fetchProducts = async () => {
      const productsCollection = await firestore.collection('products').get();
      setProducts(productsCollection.docs.map(doc => ({ id: doc.id, ...doc.data() })));
    };

    fetchProducts();
  }, []);

  return (
    <div>
      <h1>Welcome to Our Furniture Store</h1>
      <div>
        <Link to="/category/living-room">Living Room</Link>
        <Link to="/category/dining-room">Dining Room</Link>
        <Link to="/category/bedroom">Bedroom</Link>
      </div>
      <div>
        {products.map(product => (
          <div key={product.id}>
            <Link to={`/product/${product.id}`}>
              <h2>{product.name}</h2>
              <img src={product.imageUrl} alt={product.name} />
              <p>{product.price}</p>
            </Link>
          </div>
        ))}
      </div>
    </div>
  );
};

export default HomePage;
