import React, { useState } from 'react';
import { firestore } from '/firebase';  // Ensure this path matches where your firebase.js is located

const AddProductPage = () => {
  const [name, setName] = useState('');
  const [price, setPrice] = useState('');
  const [category, setCategory] = useState('Bedroom');  // Default category
  const [description, setDescription] = useState('');
  const [imageUrl, setImageUrl] = useState('');

  const handleSubmit = async (event) => {
    event.preventDefault();
    try {
      await firestore.collection('products').add({
        name,
        price: parseFloat(price),  // Ensure that the price is stored as a number
        category,
        description,
        imageUrl
      });
      alert('Product added successfully!');
      // Reset form fields after submission
      setName('');
      setPrice('');
      setCategory('Bedroom');  // Reset to default category
      setDescription('');
      setImageUrl('');
    } catch (error) {
      console.error("Error adding document: ", error);
      alert('Error adding product!');
    }
  };

  return (
    <div>
      <h1>Add Product</h1>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          value={name}
          onChange={e => setName(e.target.value)}
          placeholder="Name"
          required
        />
        <input
          type="text"
          value={price}
          onChange={e => setPrice(e.target.value)}
          placeholder="Price"
          required
        />
        <select
          value={category}
          onChange={e => setCategory(e.target.value)}
          required
        >
          <option value="Bedroom">Bedroom</option>
          <option value="Dining Room">Dining Room</option>
          <option value="Living Room">Living Room</option>
        </select>
        <textarea
          value={description}
          onChange={e => setDescription(e.target.value)}
          placeholder="Description"
          required
        />
        <input
          type="text"
          value={imageUrl}
          onChange={e => setImageUrl(e.target.value)}
          placeholder="Image URL"
          required
        />
        <button type="submit">Add Product</button>
      </form>
    </div>
  );
};

export default AddProductPage;
