import React, { useState } from 'react';
import { firestore, storage } from '../firebase';  // Ensure this path matches where your firebase.js is located
import { ref, uploadBytes, getDownloadURL } from "firebase/storage";

const AddProductPage = () => {
  const [name, setName] = useState('');
  const [price, setPrice] = useState('');
  const [category, setCategory] = useState('Bedroom');  // Default category
  const [description, setDescription] = useState('');
  const [image, setImage] = useState(null);

  const handleSubmit = async (event) => {
    event.preventDefault();
    if (image) {
      const storageRef = ref(storage, `products/${image.name}`);
      await uploadBytes(storageRef, image);
      const imageUrl = await getDownloadURL(storageRef);

      try {
        await firestore.collection('products').add({
          name,
          price: parseFloat(price),  // Ensure that the price is stored as a number
          category,
          description,
          imageUrl
        });
        alert('Product added successfully!');
        // Reset form fields after submission
        setName('');
        setPrice('');
        setCategory('Bedroom');  // Reset to default category
        setDescription('');
        setImage(null);
      } catch (error) {
        console.error("Error adding document: ", error);
        alert('Error adding product!');
      }
    } else {
      alert('Please upload an image.');
    }
  };

  return (
    <div>
      <h1>Add Product</h1>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          value={name}
          onChange={e => setName(e.target.value)}
          placeholder="Name"
          required
        />
        <input
          type="text"
          value={price}
          onChange={e => setPrice(e.target.value)}
          placeholder="Price"
          required
        />
        <select
          value={category}
          onChange={e => setCategory(e.target.value)}
          required
        >
          <option value="Bedroom">Bedroom</option>
          <option value="Dining Room">Dining Room</option>
          <option value="Living Room">Living Room</option>
        </select>
        <textarea
          value={description}
          onChange={e => setDescription(e.target.value)}
          placeholder="Description"
          required
        />
        <input
          type="file"
          accept="image/*"
          onChange={e => setImage(e.target.files[0])}
          required
        />
        <button type="submit">Add Product</button>
      </form>
    </div>
  );
};

export default AddProductPage;
