import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import { firestore } from '../firebase';
import { collection, getDocs, query, where } from "firebase/firestore";
import '../CategoryPage.css';

const CategoryPage = () => {
  const { name } = useParams();
  const [products, setProducts] = useState([]);
  const [showFullDescription, setShowFullDescription] = useState({});

  useEffect(() => {
    const fetchProducts = async () => {
      const q = query(collection(firestore, 'products'), where("category", "==", name));
      const productSnapshot = await getDocs(q);
      const productList = productSnapshot.docs.map(doc => ({
        id: doc.id,
        ...doc.data()
      }));
      setProducts(productList);
    };

    fetchProducts();
  }, [name]);

  const handleReadMore = (id) => {
    setShowFullDescription(prevState => ({ ...prevState, [id]: !prevState[id] }));
  };

  const truncateDescription = (description, id) => {
    if (showFullDescription[id]) {
      return description;
    }
    if (description.length > 100) {
      return `${description.substring(0, 100)}...`;
    }
    return description;
  };

  return (
    <div className="category-page">
      <h1>{name} Products</h1>
      <div className="products-container">
        {products.map(product => (
          <div key={product.id} className="product-card">
            <img src={product.imageUrl} alt={product.name} className="product-image" />
            <h2 className="product-name">{product.name}</h2>
            <p className="product-description">{truncateDescription(product.description, product.id)}</p>
            {product.description.length > 100 && (
              <button className="read-more-button" onClick={() => handleReadMore(product.id)}>
                {showFullDescription[product.id] ? 'Show less' : 'Read more...'}
              </button>
            )}
            <p className="product-price">Price: NPR {isNaN(product.price) ? "0" : product.price}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default CategoryPage;
